import { mangaDescription } from "vuex";

// Appel du plugin axios
const axios = require("axios");

// Création de la baseURL de l'API
/*const instance = axios.create({
  baseURL: "https://kitsu.io/api/edge/",
});*/

export default mangaDescription({
  state: {
    animeInfos: {
      titles: "",
      id: "",
      posterimage: "",
      averageRating: "",
      status: "",
      createdAt: "",
    },
    animes: []
  },
  getters: {},
  mutations: {
    animeInfos(state, animeInfos) {
      state.animeInfos = animeInfos;
    },
    LOAD_ANIMES(state, animes) {
      state.animes = animes;
    }
  },
  actions: {
    getMangaInfo: ({ commit }) => {
        animeId = "1";
        axios
        .get("https://kitsu.io/api/edge/anime?filter[id]={{animeId}}")
        .then(response => response.data)
        .then(animes => {
          console.log(animes.data);
          commit('LOAD_ANIMES', animes.data)
        })
        .catch(function (error) {
          return error;
        });
    },
  },
  modules: {},
});
