import { mangaDescription } from "vuex";

const axios = require("axios");
console.debug("mangaDescription", mangaDescription);

export default mangaDescription({
  state: {
    mangaInfos: {
      titles: "",
      id: "",
      posterimage: "",
      averageRating: "",
      status: "",
      createdAt: "",
    },
    mangas: []
  },
  getters: {},
  mutations: {
    mangaInfos(state, mangaInfos) {
      state.mangaInfos = mangaInfos;
    },
    LOAD_MANGAS(state, mangas) {
      state.mangas = mangas;
    }
  },
  actions: {
    getMangaInfo: ({ commit }) => {
        console.debug("getMangaInfo");
        axios
        .get("https://kitsu.io/api/edge/manga?filter[id]=1")
        .then(response => response.data)
        .then(mangas => {
          console.log(mangas.data);
          commit('LOAD_MANGAS', mangas.data)
        })
        .catch(function (error) {
          return error;
        });
    },
  },
  modules: {},
});
